mapper.select:
SELECT * FROM PR WHERE IO IS NULL


		 AND PR.LOT_NO=#{LOT_NO}


		ORDER BY ${ORDER}


		LIMIT #{displayPost}, #{postNum}

SELECT * FROM PR WHERE PART_NO = #{PART_NO} AND IO='O'


		 AND PR.LOT_NO=#{LOT_NO}


		ORDER BY ${ORDER}


		LIMIT #{displayPost}, #{postNum}

SELECT * FROM PR WHERE IO='O'


		 AND PR.LOT_NO=#{LOT_NO}


		ORDER BY ${ORDER}


		LIMIT #{displayPost}, #{postNum}
