mapper.select:
SELECT PR.LOT_NO , PR.PART_NO, BOM2.PART_NAME, PR.QUAN, PR.UNIT, PR.S_DATE, PR.F_DATE FROM PR, BOM2 WHERE PR.PART_NO=BOM2.PART_NO


 AND PR.LOT_NO=#{LOT_NO}


ORDER BY ${ORDER}


LIMIT #{displayPost}, #{postNum}

SELECT M_NO, M_NAME, M_QUAN FROM WH


		 WHERE M_NO=#{M_NO}


		ORDER BY ${ORDER}
